import React from "react";
import { minutesToDuration, secondsToDuration } from "../utils/duration";

export default function Display({ isTimerRunning, focusDuration, breakDuration }) {

    if(!isTimerRunning) {
        return `Focusing for ${minutesToDuration(focusDuration)}`
    }else{
        return `On Break for ${minutesToDuration(breakDuration)}`
    }

}
