import React from "react";
import { secondsToDuration } from "../utils/duration"

export default function Display({ isTimerRunning, secondsToDuration }) {




    if(isTimerRunning) {
        return `Focusing for ${secondsToDuration(focusDuration)}`
    }else{
        return `On Break for ${secondsToDuration(breakDuration)}`
    }

}
