import React, { useEffect, useState } from "react";
import classNames from "../utils/class-names";
import { minutesToDuration, secondsToDuration } from "../utils/duration";
import useInterval from "../utils/useInterval";
import Timer from "./Timer";
import Display from "./Display";

function Pomodoro() {
  // Timer starts out paused
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  // The current session - null where there is no session running
  const [ currentState, setCurrentState ] = useState("Focusing");
  const [session, setSession] = useState(null);

  // ToDo: Allow the user to adjust the focus and break duration.
  const [ focusDuration, setFocusDuration ] = useState(25);
  const [ breakDuration, setBreakDuration ] = useState(5);
  const [ focusCount, setFocusCount ] = useState(focusDuration*60)
  const [ breakCount, setBreakCount ] = useState(breakDuration*60)
  const [ disabled, setDisabled ] = useState(true);
  const [ hidden, setHidden ] = useState(false);
  const [ paused, setPaused ] = useState("");

  function playPause() {
      setIsTimerRunning((prevState) => !prevState);
      setDisabled(false)
      if(!isTimerRunning) {
        setHidden(true);
      }  
    }

    useInterval(() => {
      if(focusCount === 0 || breakCount === 0) {
      new Audio("https://bigsoundbank.com/UPLOAD/mp3/1482.mp3").play();
      // return setSession(nextSession(focusDuration, breakDuration));
      }else if(focusCount > 0){
        setFocusCount((prevCount) => prevCount - 1);
      }else if(breakCount > 0){
        setBreakCount((prevCount) => prevCount - 1);
        }
    },
      isTimerRunning ? 1000 : null
    );

    function stop() {
      setFocusCount(focusDuration * 60)
      setBreakCount(breakDuration * 60)
      setIsTimerRunning(false);
    }


  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <Timer 
            title="Focus Duration"
            value={focusDuration}
            increment={5}
            onChange={setFocusDuration}
            lowerLimit={5}
            upperLimit={60}
            disabled={isTimerRunning}
            />
          </div>
        </div>

        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <Timer 
              title="Break Duration" 
              value={breakDuration}
              increment={1}
              onChange={setBreakDuration}
              lowerLimit={1}
              upperLimit={15}
              disabled={isTimerRunning}     
              />
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            > 
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session. and disable the stop button when there is no active session */}
            {/* TODO: Disable the stop button when there is no active session */}
            <button
              type="button"
              className="btn btn-secondary"
              data-testid="stop"
              title="Stop the session"
              onClick={stop}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <div>
        {/* TODO: This area should show only when there is an active focus or break - i.e. the session is running or is paused */}
        <Display 
          disabled={disabled}
          hidden={hidden}
          currentState={currentState}
          focusDuration={focusDuration}
          breakDuration={breakDuration}
          isTimerRunning={isTimerRunning}
          focusCount={focusCount}
          breakCount={breakCount}
          paused={paused}
        />
      </div>
    </div>
  );
}

export default Pomodoro;
