import React from "react";
import { minutesToDuration } from "../utils/duration";

function Timer({
    title,
    value,
    increment,
    onChange,
    upperLimit,
    lowerLimit,
    disabled,
    decreaseFocus
}){
    return (
    <>
        <span className="input-group-text" data-testid="duration-focus">
        {/* TODO: Update this text to display the current focus session duration */}
        {title}: {minutesToDuration(value)}
            </span>
            <div className="input-group-append">
                <button
                    disabled={disabled || value === lowerLimit}
                    type="button"
                    className="btn btn-secondary"
                    data-testid="decrease-focus"
                    onClick={({descreaseFocus}) => {
                        onChange((prevValue) => {
                            if(prevValue - increment <= lowerLimit){
                                return lowerLimit;
                            }
                            return prevValue - increment 
                        })
                    }}
                >
                <span className="oi oi-minus" />
                </button>

                <button
                    disabled={disabled || value === upperLimit}
                    type="button"
                    className="btn btn-secondary"
                    data-testid="increase-focus"
                    onClick={() => {
                        onChange((prevValue) => {
                            console.log(prevValue)
                            if(prevValue + increment >= upperLimit){
                                return upperLimit;
                            }
                            return prevValue + increment
                        })    
                    }}
                >
                <span className="oi oi-plus" />
                </button>
        </div>
    </>
    )
}

export default Timer;
